define(["ngMocks","ezweb/core/filters/increaseFilter"],function(){describe("**filter tests**",function(){beforeEach(module("ezweb.core.filters")),it("words filter : words is NaN",inject(["wordsFilter",function(a){var b="truncate text strings",c=0/0,d=b,e=a(b,c);expect(d===e).toBe(!0)}])),it("words filter : words is negatif",inject(["wordsFilter",function(a){var b="truncate text strings",c=-3,d="",e=a(b,c);expect(d===e).toBe(!0)}])),it("words filter : words is positif",inject(["wordsFilter",function(a){var b="truncate text strings",c=2,d="truncate text...",e=a(b,c);expect(d===e).toBe(!0)}]))})});