define(["ngMocks","ezweb/core/services/pathsServices"],function(){"use strict";describe("** Path require service test **",function(){var a;beforeEach(module("ezweb.core.services.paths")),beforeEach(inject(["ezweb.core.services.paths.require",function(b){a=b}])),it("Should init the service",function(){expect(a).toBeDefined()}),it("Should give the right url",function(){var b=a.url("angular");expect(b).toEqual("/base/app/vendor/angular/angular")})}),describe("** Path require provider test **",function(){var a;beforeEach(module("ezweb.core.services.paths")),beforeEach(inject(["ezweb.core.services.paths",function(b){a=b}])),it("Should init the provider",function(){expect(a).toBeDefined()}),it("Should base the url on root",function(){var b=a.root("test");expect(b).toEqual("/base/../test")}),it("Should add a namespace for the features",function(){var b=a.feature("core","test");expect(b).toEqual("/base/features/core/test")}),it("Should add a namespace for the directives",function(){var b=a.directive("core","test");expect(b).toEqual("/base/directives/core/test")}),it("Should add a namespace for the layouts",function(){var b=a.layout("core","test");expect(b).toEqual("/base/layouts/core/test")})})});