define(["ngMocks","ezweb/core/services/scopeServices"],function(){"use strict";var a,b;describe("** Scope service test **",function(){beforeEach(module("ezweb.core.services.scope")),beforeEach(inject(["ezweb.core.services.appScope",function(c,d){a=c,b=d}])),it("Should init the service",function(){expect(a).toBeDefined()}),it("Should store an object in a simple namespace",function(){a.ensure("test",{simpleProperty:"go"}),expect(a.test.simpleProperty).toEqual("go")}),it("Should store an object in a complex namespace",function(){a.ensure("test.example.properties",{simpleProperty:"go",otherProperty:5}),expect(a.test.example.properties.simpleProperty).toEqual("go"),expect(a.test.example.properties.otherProperty).toEqual(5)}),it("Should not allow an empty namespace when calling the ensure method",function(){a.ensure("",{simpleProperty:"go"}),expect(a.simpleProperty).toBeUndefined()})})});